import { Component } from '@angular/core';

@Component({
  selector: 'app-date-calculator',
  templateUrl: './date-calculator.component.html',
  styleUrls: ['./date-calculator.component.css'],
})
export class DateCalculatorComponent {
  selectedDates: string = ''; // Use a string to store the input text
  dateArray: Date[] = []; 
  onDateInputChange() {
  
    const dateStrings = this.selectedDates.split(',');

    this.dateArray = dateStrings
      .map((dateString) => new Date(dateString.trim()))
      .filter((date) => !isNaN(date.getTime())); 

    this.selectedDates = dateStrings.join(', ');
  }

  calculateDaysInDates() {
    const daysInDates: number[] = this.dateArray.map((date) => {
      return this.calculateDaysFromDate(date);
    });
    console.log('Days in Dates:', daysInDates);
  }

  calculateDaysFromDate(date: Date): number {
    const currentDate = new Date();
    const difference = date.getTime() - currentDate.getTime();
    const daysDifference = Math.floor(difference / (1000 * 60 * 60 * 24));
    return daysDifference;
  }

  getDaysInMonth() {
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const lastDay = new Date(year, month + 1, 0);
    const totalDays = lastDay.getDate();
    console.log('Total days in the current month:', totalDays);
  }
}
